const e=JSON.parse('{"key":"v-3c391267","path":"/07.language-extensions/01.syntax-highlight-guide.html","title":"语法高亮","lang":"zh-CN","frontmatter":{"summary":"语法高亮 --- 语法高亮决定源代码的颜色和样式，它主要负责关键字（如javascript中的if，for）、字符串、注释、变量名等等语法的着色工作。 语法高亮由两部分工作组成： 分词：将文本分割为一系列符号（包括单词和标点）; 主题化：然后根据主题或用户设置，对符号进行着色添加样式; 在本章开始之前，建议你先玩一下 作用域检查器 工具看看文件中的符号都长什","head":[["meta",{"property":"og:url","content":"https://oorzc.cn/07.language-extensions/01.syntax-highlight-guide.html"}],["meta",{"property":"og:site_name","content":"vscode插件开发文档"}],["meta",{"property":"og:title","content":"语法高亮"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}]]},"excerpt":"","headers":[{"level":2,"title":"分词","slug":"分词","link":"#分词","children":[{"level":3,"title":"TextMate 语法","slug":"textmate-语法","link":"#textmate-语法","children":[]},{"level":3,"title":"配置基本语法","slug":"配置基本语法","link":"#配置基本语法","children":[]},{"level":3,"title":"嵌入式语言","slug":"嵌入式语言","link":"#嵌入式语言","children":[]},{"level":3,"title":"开发全新的语法插件","slug":"开发全新的语法插件","link":"#开发全新的语法插件","children":[]}]},{"level":2,"title":"主题化","slug":"主题化","link":"#主题化","children":[]},{"level":2,"title":"作用域检查器","slug":"作用域检查器","link":"#作用域检查器","children":[]}],"git":{"createdTime":null,"updatedTime":null,"contributors":[]},"readingTime":{"minutes":11.45,"words":3436},"filePathRelative":"07.language-extensions/01.syntax-highlight-guide.md"}');export{e as data};
